import cv2""" ROI (region of interest)  targeting a specific pattern and put it at a specific place.."""messi_img = cv2.imread('Photos/messi_img.jpeg')lena_img = cv2.imread('Photos/Lena.png')openCV_img = cv2.imread('Photos/openCV_img.jpeg')# ball = messi_img[640:770, 490:630]# messi_img[620: 750, 100:240] = ball# cv2.imshow('ball', ball)# cv2.imshow('messi_img', messi_img)# def click_event(event, x,y, flag, params):#     if event == cv2.EVENT_LBUTTONDOWN:  # Left click#         strXY = str(x) + ',' + str(y)#         cv2.imshow('Image', messi_img)#         print(strXY)# cv2.imshow('Image', messi_img)# cv2.setMouseCallback('Image', click_event)""" Merging 2 images """# first check their shape. if not same it won't workprint(messi_img.shape)print(openCV_img.shape)# try to add them (but for sure it will give u an error)# merged_img = cv2.add(messi_img, lena_img)# cv2.imshow('merged_img', merged_img)# need to be resizedmessi_resized_img = cv2.resize(messi_img, (712, 712))openCV_resized_img = cv2.resize(openCV_img, (712, 712))resized_merged_img = cv2.add(messi_resized_img, openCV_resized_img)cv2.imshow('resized_merged_img', resized_merged_img)resized_merged_img2 = cv2.addWeighted(messi_resized_img, .8, openCV_resized_img, .2, 0)cv2.imshow('resized_merged_img2', resized_merged_img2)cv2.waitKey(0)cv2.destroyAllWindows()