import cv2"""If you got an error read thisThe problem is that you are trying to use adaptive thresholding to an image that is not in greyscale. And the function only works with a greyscale images."""img = cv2.imread('Photos/sudoku_img.jpeg')gray_img = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)_, simple_threshold = cv2.threshold(img, 127, 255, cv2.THRESH_BINARY)  # > 127 to 0 else to 255adaptive_thr_mean = cv2.adaptiveThreshold(gray_img, 255, cv2.ADAPTIVE_THRESH_MEAN_C, cv2.THRESH_BINARY, 11, 2)adaptive_thr_guassi = cv2.adaptiveThreshold(gray_img, 255, cv2.ADAPTIVE_THRESH_GAUSSIAN_C, cv2.THRESH_BINARY, 199, 5)cv2.imshow('img', img)cv2.imshow('threshed_img_5',  simple_threshold)cv2.imshow('adaptive_thr_mean',  adaptive_thr_mean)cv2.imshow('adaptive_thr_guassi',  adaptive_thr_guassi)cv2.waitKey(0)cv2.destroyAllWindows()