import cv2import matplotlib.pyplot as pltimport numpy as npmessi_img = cv2.imread('Photos/messi_img.jpeg', 0)cv2.imshow('messi_img', messi_img)""" These is the better edge detection function ...... """canny_img = cv2.Canny(messi_img, 100, 200)names  = ["messi_img", 'canny_img']images = [messi_img, canny_img]for i in range(len(names)):    plt.subplot(1, 2, i+1), plt.imshow(images[i], 'gray')    plt.title(names[i])    plt.xticks([]), plt.yticks([])plt.show()cv2.waitKey(0)cv2.destroyAllWindows()