import cv2import matplotlib.pyplot as pltlena_img = cv2.imread('Photos/Lena.png')cv2.imshow('lena_img', lena_img)# plt will give as d/f color of the img,  cuz plt read an img RBG, but openCV read an img BGR# SO we need to convert it to RGBconverted_img_toRBG = cv2.cvtColor(lena_img, cv2.COLOR_BGR2RGB)plt.imshow(converted_img_toRBG)plt.show()cv2.waitKey(0)cv2.destroyAllWindows()