import cv2import matplotlib.pyplot as pltimport numpy as np""" masking or thresholding means converting an image to a binary...."""# gray_img = cv2.imread('Photos/smarties.png', cv2.IMREAD_GRAYSCALE)  # 0 --> grayturtle_img = cv2.imread('Photos/turtle.png')  # 0 --> graycv2.imshow('original_img', turtle_img)_, thresh_masked = cv2.threshold(turtle_img, 220, 255, cv2.THRESH_BINARY_INV) # converting it to a binary# To remove the black color inside the white smarties use dilationdilation = cv2.dilate(thresh_masked, kernel=np.ones((2, 3), np.uint8), iterations=3)erosion = cv2.erode(thresh_masked, kernel=np.ones((2,2), np.uint8), iterations=1)opening = cv2.morphologyEx(thresh_masked, cv2.MORPH_OPEN, kernel=np.ones((2,2), np.uint8))closing = cv2.morphologyEx(thresh_masked, cv2.MORPH_CLOSE, kernel=np.ones((2,2), np.uint8))names = ["turtle_img", "masked", 'dilation', 'erosion', 'opening', 'closing']images = [turtle_img, thresh_masked, dilation, erosion, opening, closing]for i in range(len(images)):    plt.subplot(2, 3, i+1), plt.imshow(images[i], 'gray')  # 2, 3 --> col and row    plt.title(names[i])    plt.xticks([]), plt.yticks([])plt.show()cv2.waitKey(0)cv2.destroyAllWindows()