# you have  a string and a num ---> make the given string n times.def n_times_str(str, n):    mul_str = ''    for i in range(n):        mul_str += str * i    return mul_str# result = n_times_str('Tesfit', 3)## print(result)arr = [4,3,2,3,4,5,7,5,3,2,4,1,2,3,4,5,6,8]N = 10newarr = []for i in range(len(arr)-1):    if(arr[i]+1 == arr[i+1]):        newarr += [arr[i]]print(newarr)# Find the given nums in sequences in the give arraydef array123(nums):    for i in range(len(nums) - 1):        if nums[i+2] <= len(nums):            print(i)            firNum = nums[i]            secNum = nums[i + 1]            thrNum = nums[i + 2]            if firNum == 1 and secNum == 2 and thrNum == 3:                return True    return Falser = array123([20, 30, 10, 1, 2, 3, 4, 54, 4])# print(r)# my_dic=[#     {"name":"siem",#      "age" : 22,#      "invitation":True,#      "student_ID " :1,#      "grade": 11},##       {"name":"royal",#      "age":28,#      "invitation":True,#      "student_ID ":2,#      "grade": 11},##       {"name":"tesfit",#      "age":26,#      "invitation":True,#      "student_ID ":3,#      "grade": 11},##       {"name":"john",#      "age":20,#      "invitation":True,#      "student_ID ":4,#      "grade": 10},##       {"name":"smith",#      "age":20,#      "invitation":True,#      "student_ID ":5,#      "grade" : 10}##  ]## for i in range ( 4 ):##     if my_dic[i]["name"] not in ["royal", "siem", "tesfit", "smith", "john"]:#           print("its not allowed")#           break## for i in range ( 4 ):##     if my_dic[i]["age"] not in [20,22,28,26]:##         print("its not allowed")#         break## for i in range ( 4 ):#     if my_dic[i]["invitation"] == True:#         print("its not allowed")#         break## for i in range ( 4 ):###     if my_dic[i]["student_ID"] not in [1,2,3,4,5]:#         print("its not allowed")#         break## for i in range ( 4 ):#     if 