import cv2def nothing(x):    print(x)cv2.namedWindow('lena_img')cv2.createTrackbar('Current-position', 'lena_img', 10, 400, nothing)switch = 'color/gray'cv2.createTrackbar(switch, 'lena_img', 0, 1, nothing)while True:    lena_img = cv2.imread('Photos/Lena.png')  # not clear why we are placing it here...!    if cv2.waitKey(1) & 0xFF == 27:        break    position = cv2.getTrackbarPos('Current-position', 'lena_img')    cv2.putText(lena_img, str(position), (50, 50), cv2.FONT_HERSHEY_SIMPLEX, 2, (0, 0, 0), 1)    switcher = cv2.getTrackbarPos(switch, 'lena_img')    if switcher == 0:        pass    else:        lena_img = cv2.cvtColor(lena_img, cv2.COLOR_BGR2GRAY)    cv2.imshow('lena_img', lena_img)cv2.destroyAllWindows()""" THE above code will show us how and when a trackbar can be used...."""